INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('deepseek', 'Deepseek', 'DeepSeek大语言模型算法是北京深度求索人工智能基础技术研究有限公司推出的深度合成服务算法，应用于智能对话场景，服务于企业端客户，根据用户输入的文本数据，通过大语言模型生成符合用户需求的文本、代码等内容', '/static/images/model/deepseek.svg', 'llm');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('default', 'WENKB', 'WENKB，一个创新的大模型知识库应用，为您提供了一个全面、智能的解决方案，让您的知识管理变得更加简单、高效。', '/static/images/model/wenkb.png', 'text-embedding');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('moonshot', '月之暗面', 'Moonshot的文本生成模型（指moonshot-v1）是训练用于理解自然语言和书面语言的，它可以根据输入生成文本输出。对模型的输入也被称为“prompt”。通常我们建议您提供明确的指令以及给出一些范例，来让模型能够完成既定的任务，设计 prompt 本质上就是学会如何“训练”模型。moonshot-v1模型可以用于各种任务，包括内容或代码生成、摘要、对话、创意写作等。', '/static/images/model/moonshot.png', 'llm');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('nvidia', 'Nvidia', 'API Catalog', '/static/images/model/nvidia.png', 'llm,text-embedding,rerank');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('ollama', 'Ollama', 'Ollama 是一个强大的框架，设计用于在 Docker 容器中部署 LLM。Ollama 的主要功能是在 Docker 容器内部署和管理 LLM 的促进者，它使该过程变得非常简单。它帮助用户快速在本地运行大模型，通过简单的安装指令，可以让用户执行一条命令就在本地运行开源大型语言模型。', '/static/images/model/ollama.svg', 'llm,text-embedding');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('openai', 'OpenAI', 'OpenAI 提供的模型，例如 GPT-3.5-Turbo 和 GPT-4。', '/static/images/model/openai.svg', 'llm,text-embedding,tts,speech2text');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('tongyi', '通义千问', '通义意为“通情，达义”，具备全副AI能力，致力于成为人们的工作、学习、生活助手。 功能包括多轮对话、文案创作、逻辑推理、多模态理解、多语言支持，能够跟人类进行多轮的交互，也融入了多模态的知识理解，且有文案创作能力，能够续写小说，编写邮件等。', '/static/images/model/tongyi.png', 'llm,text-embedding,tts');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('zhipuai', '智谱AI', '智谱AI是由清华大学计算机系技术成果转化而来的公司，致力于打造新一代认知智能通用模型。公司合作研发了双语千亿级超大规模预训练模型GLM-130B，并构建了高精度通用知识图谱，形成数据与知识双轮驱动的认知引擎，基于此模型打造了ChatGLM（chatglm.cn）。此外，智谱AI还推出了认知大模型平台Bigmodel.ai，包括CodeGeeX和CogView等产品，提供智能API服务，链接物理世界的亿级用户、赋能元宇宙数字人、成为具身机器人的基座，赋予机器像人一样“思考”的能力。', '/static/images/model/zhipuai.svg', 'llm,text-embedding');
